import { RecruiterType } from '@/src/types/data.types';
import { supabase } from '@/src/utils/supabase/client';

export async function updateRecruiter(id: string, obj: RecruiterType) {
  const { data, error } = await supabase
    .from('recruiter')
    .update({ ...obj })
    .eq('id', id)
    .select()
    .single();
  if (!error) {
    return data;
  }
}

