import { Stack } from '@mui/material';
import { useRouter } from 'next/router';
import React, { useRef, useState } from 'react';

import { LoginAtsInfo, RcFormRadio, RecCompanyDetails } from '@/devlink2';
import { useAuthDetails } from '@/src/context/AuthContext/AuthContext';
import { useSignupDetails } from '@/src/context/SingupContext/SignupContext';
import { SocialsType } from '@/src/types/data.types';
import { YTransform } from '@/src/utils/framer-motions/Animation';
import { supabase } from '@/src/utils/supabaseClient';

import { stepObj } from '../SlideSignup/utils';
import AUIButton from '../../Common/AUIButton';
import UITextField from '../../Common/UITextField';
import UITypography from '../../Common/UITypography';

function SelectAtsSystem() {
  const { setStep } = useSignupDetails();
  const { recruiter, setRecruiter } = useAuthDetails();

  const router = useRouter();

  const [selectedObject, setSelectedObject] = useState(null);
  const [textCheck, setTextCheck] = useState(false);
  const inputRef = useRef(null);

  async function handleContinue() {
    if (
      (selectedObject !== null && selectedObject !== 'Other') ||
      (selectedObject === 'Other' && inputRef.current.value !== '')
    ) {
      setTextCheck(false);
      const { data, error } = await supabase
        .from('recruiter')
        .update({
          ats_familiar:
            selectedObject === 'Other'
              ? inputRef.current.value
              : selectedObject,
        })
        .eq('id', recruiter.id)
        .select();
      if (!error) {
        setRecruiter({ ...data[0], socials: data[0].socials as SocialsType });
        router.push(`?step=${stepObj.useGoal}`, undefined, {
          shallow: true,
        });
        setStep(stepObj.useGoal);
      }
    }

    if (selectedObject === 'Other' && inputRef.current.value === '') {
      setTextCheck(true);
    }
  }
  return (
    <>
      <RecCompanyDetails
        slotMain={
          <LoginAtsInfo
            slotRadioButtons={
              <>
                {object.map((ele, i) => {
                  return (
                    <RcFormRadio
                      isClicked={selectedObject === ele.name}
                      onclickRadio={{
                        onClick: () => {
                          setSelectedObject(ele.name);
                        },
                      }}
                      key={i}
                      isImageAvailabe={ele.image ? true : false}
                      slotImage={ele.image}
                      name={ele.name}
                    />
                  );
                })}
              </>
            }
            slotAdditionalInfo={
              <>
                {selectedObject === 'Other' && (
                  <>
                    <YTransform uniqueKey={selectedObject}>
                      <Stack spacing={'13px'}>
                        <UITypography fontBold={'normal'}>
                          Kindly provide the name of the ATS system you are
                          using.
                        </UITypography>
                        <UITextField
                          ref={inputRef}
                          //   onChange={(ele) => {
                          //     console.log('error');
                          //     setSelectedObject((pre) => {
                          //       pre.name = ele.target.value;
                          //       return pre;
                          //     });
                          //   }}
                          placeholder='ATS system name'
                          error={textCheck}
                          helperText='Please give name of the ATS system'
                        />
                      </Stack>
                    </YTransform>
                  </>
                )}
                <Stack mt={'13px'}>
                  <AUIButton
                    onClick={handleContinue}
                    disabled={selectedObject === null}
                  >
                    Continue
                  </AUIButton>
                </Stack>
              </>
            }
          />
        }
      />
    </>
  );
}

export default SelectAtsSystem;

