INSERT INTO "public"."company_email_template" ("id", "created_at", "recruiter_id", "subject", "body", "from_name", "type") VALUES
	('cc12acb9-cfdf-480f-ae65-f5e043290e8d', '2024-08-02 14:33:35.693236+00', 'edd40dc6-b173-4593-a6fa-8461a5e208f8', '', '', '', 'interviewerConfirmation_slack_interviewers'),
	('07725e1f-a774-404f-a000-ca3c85ab3f26', '2024-08-02 14:33:35.693236+00', 'edd40dc6-b173-4593-a6fa-8461a5e208f8', '', '', '', 'onTrainingComplete_slack_approverForTraineeMeetingQualification'),
	('1b62f79a-202e-45c8-ba50-65a878673cc7', '2024-08-02 14:33:35.693236+00', 'edd40dc6-b173-4593-a6fa-8461a5e208f8', '<p>Congratulations on Your Qualification</p>', '<p>Hi <span class="temp-variable" data-type="temp-variable" data-id="traineeName">{{traineeName}}</span>,</p><p></p><p>Congratulations, <span class="temp-variable" data-type="temp-variable" data-id="traineeName">{{traineeName}}</span> ! You are now qualified to conduct interviews for <span class="temp-variable" data-type="temp-variable" data-id="interviewType">{{interviewType}}</span>.</p><p></p><p>From, <span class="temp-variable" data-type="temp-variable" data-id="approverName">{{approverName}}</span></p>', '{{companyName}}', 'onQualified_email_trainee'),
	('4e4049b5-7f2b-49f0-9d18-e1a99e61fdde', '2024-08-02 14:33:35.693236+00', 'edd40dc6-b173-4593-a6fa-8461a5e208f8', '<p>Reminder: Upcoming Interview for <span class="temp-variable" data-type="temp-variable" data-id="jobRole">{{jobRole}}</span> with <span class="temp-variable" data-type="temp-variable" data-id="candidateFirstName">{{candidateFirstName}}</span> </p>', '<p>Dear <span class="temp-variable" data-type="temp-variable" data-id="organizerFirstName">{{organizerFirstName}}</span> ,</p><p></p><p>This is a friendly reminder about the upcoming interview for the <span class="temp-variable" data-type="temp-variable" data-id="jobRole">{{jobRole}}</span> with <span class="temp-variable" data-type="temp-variable" data-id="candidateFirstName">{{candidateFirstName}}</span>.</p><p></p><p>Please ensure everything is set for a smooth interview process.</p><p>Please find the details of your interview below:</p><p></p><p>Best regards,</p><p>Aglint AI Team</p>', 'Aglint Ai', 'interviewStart_email_organizer'),
	('f0b8a659-a334-42ef-896d-6672c66c2aad', '2024-08-02 14:33:35.693236+00', 'edd40dc6-b173-4593-a6fa-8461a5e208f8', 'Interview reminder', '<p><strong>Scheduled with candidate :</strong><br><a target="_blank" rel="noopener noreferrer" class="c-link" href="https://dev.aglinthq.com/scheduling/view?meeting_id=5ad7e0df-be62-4461-a069-33e884b70c4f&amp;tab=candidate_details"><strong>Ashis Sarthak Singh - Staff Frontend Engineer</strong></a></p><p><strong>Meeting Place :</strong> In Person Meeting<br><strong>Meeting Time :</strong> July 15 03:30 AM - 04:00 AM IST<br><strong>Duration :</strong> 30 Minute      </p>', '', 'interviewStart_slack_interviewers'),
	('0e8371ac-0c5b-471e-808f-ceab774aaf2f', '2024-08-02 14:33:35.693236+00', 'edd40dc6-b173-4593-a6fa-8461a5e208f8', '<p>Interview Confirmation for <span class="temp-variable" data-type="temp-variable" data-id="jobRole">{{jobRole}}</span> with <span class="temp-variable" data-type="temp-variable" data-id="candidateFirstName">{{candidateFirstName}}</span> </p>', '<p>Dear <span class="temp-variable" data-type="temp-variable" data-id="organizerFirstName">{{organizerFirstName}}</span> </p><p>Please find the details for the interview below:</p><p><strong>Job:</strong> <span class="temp-variable" data-type="temp-variable" data-id="jobRole">{{jobRole}}</span></p><p><strong>Candidate name:</strong> <span class="temp-variable" data-type="temp-variable" data-id="candidateName">{{candidateName}}</span> <br></p><p>Thank you,</p><p>Aglint AI Team</p>', 'Aglint Ai', 'confInterview_email_organizer'),
	('2b5edc4d-14e8-4078-be5c-b423858fc55d', '2024-08-02 14:33:35.693236+00', 'edd40dc6-b173-4593-a6fa-8461a5e208f8', '<p>Reminder: Feedback Required for <span class="temp-variable" data-type="temp-variable" data-id="jobRole">{{jobRole}}</span> Interview </p>', '<p>Dear <span class="temp-variable" data-type="temp-variable" data-id="interviewerFirstName">{{interviewerFirstName}}</span>,</p><p>This is a friendly reminder to provide your feedback for the recent interview you conducted with <span class="temp-variable" data-type="temp-variable" data-id="candidateName">{{candidateName}}</span>  for the <span class="temp-variable" data-type="temp-variable" data-id="jobRole">{{jobRole}}</span> position at <span class="temp-variable" data-type="temp-variable" data-id="companyName">{{companyName}}</span> .</p><p></p><p>Please submit your feedback at your earliest convenience.</p><p>Thank you for your time and assistance.</p><p>Best regards,</p><p>Aglint AI Team</p>', 'Aglint Ai', 'candidateBook_email_interviewerForFeedback'),
