	('b80401ae-74ac-49dc-ba1d-ad1717a0cb7d', '2024-08-02 14:33:35.907787+00', '14faa4e2-7818-4635-870f-0dd93d6d0578', 1, '{"body": "<p><strong>Scheduled with candidate :</strong><br><a target=\"_blank\" rel=\"noopener noreferrer\" class=\"c-link\" href=\"https://dev.aglinthq.com/scheduling/view?meeting_id=5ad7e0df-be62-4461-a069-33e884b70c4f&amp;tab=candidate_details\"><strong>Ashis Sarthak Singh - Staff Frontend Engineer</strong></a></p><p><strong>Meeting Place :</strong> In Person Meeting<br><strong>Meeting Time :</strong> July 15 03:30 AM - 04:00 AM IST<br><strong>Duration :</strong> 30 Minute      </p>", "subject": "Interview reminder"}', 'interviewStart_slack_interviewers'),
	('84a595a9-b911-4ed5-9377-d0b4c707d4e5', '2024-08-02 14:33:36.085043+00', 'eb175a15-cdbe-42f2-baaa-7579d0d9cafc', 0, '{"body": "", "subject": ""}', 'candidateBook_slack_interviewerForConfirmation'),
	('708c6345-01e0-4857-9672-d862150397a2', '2024-08-02 14:33:36.094825+00', '5f411c93-0e62-4177-9158-37f4bbb5911a', 0, '{"body": "<p>Dear <span class=\"temp-variable\" data-type=\"temp-variable\" data-id=\"candidateFirstName\">{{candidateFirstName}}</span>,</p><p></p><p style=\"text-align: start\">I hope this message finds you well.</p><p style=\"text-align: start\">I am writing to follow up on my previous email regarding the interview for the <span class=\"temp-variable\" data-type=\"temp-variable\" data-id=\"jobRole\">{{jobRole}}</span> position at <span class=\"temp-variable\" data-type=\"temp-variable\" data-id=\"companyName\">{{companyName}}</span>. We are very interested in discussing your application and learning more about your experiences.</p><p style=\"text-align: start\">If you could please click on the link below to select your availability for an interview, it would be greatly appreciated .</p><p style=\"text-align: start\">If you have any questions or need further information, please feel free to reach out.</p><p style=\"text-align: start\">Thank you, and I look forward to hearing from you soon.</p><p style=\"text-align: start\"></p><p style=\"text-align: start\">Best regards,</p><p style=\"text-align: start\"><span class=\"temp-variable\" data-type=\"temp-variable\" data-id=\"organizerName\">{{organizerName}}</span></p><p><span class=\"temp-variable\" data-type=\"temp-variable\" data-id=\"companyName\">{{companyName}}</span> Recruitment Team</p>", "subject": "<p>Reminder: Provide Your Availability for the <span class=\"temp-variable\" data-type=\"temp-variable\" data-id=\"jobRole\">{{jobRole}}</span> Position at <span class=\"temp-variable\" data-type=\"temp-variable\" data-id=\"companyName\">{{companyName}}</span> </p>"}', 'sendAvailReqReminder_email_applicant'),
